mapper.insert:
update ta_point set
         remarks_name = #{remarksName},
         mod_date=now(),
         mod_user = #{modUser}
         where point_name =#{pointName}

mapper.update:
update ta_point set
         point_name = #{pointName},
         mod_date=now(),
         state=-1,
         mod_user = #{modUser}
         where point_id = #{pointId}

mapper.select:
select * from ta_point where state = 1
and point_name like CONCAT('%',#{point_name},'%')
and remarks_name like CONCAT('%',#{remarks_name},'%')
and point_type = #{point_type}
order by point_id desc limit #{pageNum},#{pageSize}
